'use strict';

// MODULES //
var cos = require('@stdlib/math/base/special/cos/lib');

// MAIN //

/**
* Computes the secant of a number.
*
* @param {number} x - input value (in radians)
* @returns {number} secant
*
* @example
* var v = sec( 0.0 );
* // returns 1.0
*
* @example
* var v = sec( 3.141592653589793/4.0 );
* // returns ~1.4144
*
* @example
* var v = sec( -3.141592653589793/6.0 );
* // returns ~1.1547
*
* @example
* var v = sec( NaN );
* // returns NaN
*/
function sec( x ) {
	return 1 / cos(x);
}


// EXPORTS //

module.exports = sec;
